
public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
        int b=30;
        System.out.println("Before Swapping"+a+" "+b);
        a=a+b;//a=50,b=30
        b=a-b;//b=20
        a=a-b;
        System.out.println("After Swapping"+a+" "+b);

        

    }


	}


